import React, { Component } from 'react'

class Buscador extends Component {
    constructor(props){
        super(props)
        this.state = {
            valorInput:'',
            tipoDeBusqueda:'album',
        }
    }

    preventSubmit(event){
        event.preventDefault()
    }

    controlarInput(event){
        this.setState({
            valorInput: event.target.value
        }, ()=> console.log(this.state.valorInput))
    }

    controlarRadioButtons(event){
      this.setState({
        tipoDeBusqueda:event.target.value
      })
      .then(()=> console.log('Sirve con promesas'))
    }
  render() {
    return (
      <form onSubmit={(event)=> this.prevenirSubmit(event)}>
        <input type='text' onChange={(event)=> this.controlarInput(event)} value={this.state.valorInput}/>
        <input type='radio' name='tipoDeBusqueda' onClick={(event) => this.controlarRadioButtons(event)} value='album' />
        <input type='radio' name='tipoDeBusqueda' onClick={(event) => this.controlarRadioButtons(event)} value='artist' />
      </form>
    )
  }
}

export default Buscador
