import React, { Component } from 'react'
import Buscador from '../../components/Buscador/Buscador'
import Tarjetas from '../../components/Tarjetas/Tarjetas'


class Home extends Component {
    constructor(props){
        super(props)
        this.state={
            topList:{},
            albums:{},
            ready:false,
            busqueda:'',
            tipoDeBusqueda:''
        }
    }

    buscadorInteligente(name, tipo){
        this.setState({
            busqueda:name,
            tipoDeBusqueda: tipo 
        }, ()=> {
            if(this.state.busqueda !== ''){
                fetch(`https://api.deezer.com/search/${this.state.tipoDeBusqueda}?q=${this.state.busqueda}`)
                .then(resp => resp.json())
                .then(data => console.log(data))
                .catch(err => console.log(err))
            }
        })
        // fetch
    }

    componentDidMount(){
        fetch('https://thingproxy.freeboard.io/fetch/https://api.deezer.com/chart/0/tracks&top?limit=10')
        .then(resp => resp.json())
        .then(data => this.setState({
            topList: data.data,
            ready:true
        }))
        .catch(err => console.log(err))
    }


  render() {
    return (
        <>
        <h1> Soy la Home</h1>
        <Buscador funcionQueBusca={(nombre, tipo)=> this.buscadorInteligente(nombre, tipo)} />
        {
            this.state.ready ?
                <>
                    <Tarjetas info={this.state.topList} />
                </> :
            'Cargando..'
        }
        </>
    )
  }
}

export default Home
