import Tarjetas from '../../components/Tarjetas/Tarjetas'
import BuscadorEspecial from '../../components/BuscadorEspecial/BuscadorEspecial'

class Home extends Component {
    constructor(props){
        super(props)
        this.state={
            topList:{},
            albums:{},
            resultadosBusqueda:[],
            ready:false,
        }
    }

    // buscadorInteligente(name, tipo){
